import { Button, FormControl, Grid, TextField } from "@mui/material"
import { Box } from "@mui/system"
import "../assets/RegisterPage.css"
import { useForm } from 'react-hook-form';
import { useState } from "react";


export const RegisterPage = () => {
    const { register, handleSubmit, formState: { errors } } = useForm(); //form validation
    //state defined
    const [values, setValues] = useState({
        firstName: '',
        lastName: '',
        address: '',
        email: '',
        password: '',
        confirmPassword: '',
    });
    const handleChange = (prop) => (event) => {
        setValues({ ...values, [prop]: event.target.value });
    };

    const onSubmit = () => {
        console.log("The Data Should Be Displayed: ", values)
    }

    return (
        <>
            <Box sx={{ height: "100vh", backgroundColor: '#AC4425', display: "flex", flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
                <Box className="responsive" sx={{
                    width: "40%", display: 'flex', flex: 'wrap', flexDirection: 'column', justifyContent: 'center', padding: '30px',
                    boxShadow: 'rgba(0, 0, 0, 0.35) 0px 5px 15px;',
                    alignItems: 'center', backgroundColor: '#F0F2B6',
                    borderRadius: '20px'
                }} >
                    <h1 style={{ textAlign: 'center', marginBottom: '30px', color: '#AC4425' }}>Sign Up to the System</h1>
                    <FormControl sx={{ display: 'flex', flex: 'wrap', width: '100%', alignItems:"center" }}>
                        <Grid container sx={{ display: 'flex', flex: 'wrap', justifyContent: 'space-around' }}>
                            <Grid item sm={12} md={5} sx={{ width: 1, display: 'flex', flexDirection: 'column' }}>
                                <TextField sx={{
                                    marginBottom: 3
                                }}
                                    label="First Name: "
                                    name="fName"
                                    variant="outlined" value={values.firstName} 
                                    {...register("fName", { required: true, pattern: /\S+@\S+\.\S+/ })}
                                    onChange={handleChange("firstName")}  />

                                <TextField sx={{ marginBottom: 3 }}
                                    label="Last Name: "
                                    name="lName"
                                    variant="outlined" value={values.lastName} onChange={handleChange("lastName")} />
                                <TextField sx={{ marginBottom: 3 }}
                                    label="Address: "
                                    variant="outlined" value={values.address} onChange={handleChange("address")} />
                            </Grid>

                            <Grid item sm={12} md={5} sx={{ width: 1, display: 'flex', flexDirection: 'column' }}>
                                <TextField sx={{ marginBottom: 3 }}
                                    type='email'
                                    label="Email: " variant="outlined" value={values.email} onChange={handleChange("email")}/>
                                <TextField sx={{ marginBottom: 3 }} type='password' label="Password: "
                                    variant="outlined" value={values.password} onChange={handleChange("password")} />
                                <TextField sx={{ marginBottom: 3 }} type='password' label="Confirm Password: "
                                    variant="outlined" value={values.confirmPassword} onChange={handleChange("confirmPassword")}  />
                            </Grid>
                        </Grid>
                        
                    <Button onClick={handleSubmit(onSubmit)} variant="contained" sx={{ backgroundColor: '#224B0C', minWidth:"100px", maxWidth:"200px"}} type="submit">Sign Up</Button>
                    </FormControl>
                    
                    <a href="/" style={{ textDecoration: 'none', color: '#AC4425', marginTop: '20px' }}>Do you already have an account?</a>
                </Box>
            </Box>
        </>
    )
}
