import { Box } from "@mui/system";
import NavBar from "../Component/navbar";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import { useEffect, useState } from "react";
import axios from "axios";
ChartJS.register(ArcElement, Tooltip, Legend);

export const DashBoard = () => {
    const [user, setUser] = useState([])
    useEffect(() => {
        axios.get(`http://localhost:3002/users`)
        .then(function (response) {
            
            setUser(response.data)
        })
        .catch(function (error) {

            console.log(error);
        })
    },[])
    console.log("the data",user)
    const Number = user.length
    console.log("number data", Number)
    const data = {
        labels: ['Male', 'Female', 'Bisexual'],
        datasets: [
            {
                labels: 'genders',
                data: [12, 19, 3],
                backgroundColor: [
                    'rgba(255, 99, 132, 0.2)',
                    'rgba(54, 162, 235, 0.2)',
                    'rgba(221,160,221, 0.18)'
                ],
                borderColor: [
                    'rgba(255, 99, 132, 1)',
                    'rgba(54, 162, 235, 1)',
                    'rgba(221,160,221,  1)'
                ],
                borderWidth: 1,
            },
        ],
    };

    return (
        <>
            <Box sx={{ display: "flex", width:"100wv" }}>
                <Box>
                    <NavBar />
                    
                </Box>

                <Box sx={{ top: "100px", position: "relative", width:"100%" }}>
                    
                    <Box sx={{display:"flex", width:"90%", justifyContent:"space-between"}}>
                    <Box sx={{ maxWidth: 345}}> <Pie data={data} /> </Box>
                    
                    <Card sx={{ maxWidth: 345 }}>
                        <CardContent>
                            <Typography gutterBottom variant="h5" component="div">
                                Total Number Of Products
                            </Typography>
                            <Typography variant="body2" color="text.secondary">
                                NUMBERS RAKHNI HOOO
                            </Typography>
                        </CardContent>
                    </Card>

                    <Card sx={{ maxWidth: 345 }}>
                        <CardContent>
                            <Typography gutterBottom variant="h5" component="div">
                                Total Number Of Customers
                            </Typography>
                            <Typography variant="body2" color="text.secondary">
                                {Number}
                            </Typography>
                        </CardContent>
                    </Card>
                    </Box>
                </Box>
            </Box>
        </>

    )
};
